import React, { Component } from 'react';
import {View, Text, Platform} from 'react-native';
import Canvas, {Image as CanvasImage} from 'react-native-canvas';

var width = 800;
var height = 800;

export default class DrawCanvas extends Component {

	constructor(props) {
		this.qRCodeCanvasRef1 = React.createRef();
		this.backgroundCanvasRef1 = React.createRef();
	  }

    onDraw = (event) => {
        console.log("hello")
    }
    handleCanvas = (canvas) => {

    console.log("canvas is", canvas)
      const ctx = canvas.getContext('2d');
      const image = new CanvasImage(canvas);


      ctx.fillStyle = 'purple';
      ctx.fillRect(50, 0, 100, 20);
      ctx.lineWidth = 1;

      image.addEventListener("touchstart", this.onDraw, false)


    }


    render() {
      if (Platform.OS === "web") {
        return <canvas style={{width, height}} ref={this.handleCanvas} />

    }

    return (
        <Canvas style={{ width, height }} ref={this.handleCanvas} />
    )
    }


  }
